import axios from "axios";
export default {
  name: Contact,
  data() {
    return {
      name: "",
      email: "",
      message: ""
    };
  },
  methods: {
    async sendEmail() {
      const formData = {
        name: this.name,
        email: this.email,
        message: this.message
      };
      try {
        await axios.post("/send-email", formData);
        alert("Email sent successfully!");
      } catch (error) {
        console.error(error);
        alert("An error occurred while sending the email. Please try again later.");
      }
    }
  }
};
