import "core-js/modules/es.array.push.js";
import axios from "axios";
export default {
  name: "Charts",
  data() {
    return {
      countries: [],
      data: []
    };
  },
  created() {
    // Get the countries from the url
    this.countries = this.$route.query.countries.split(",");
    // console.log(this.countries);

    // Fetch the data for each country
    this.countries.forEach(country => {
      axios.get(`https://api.example.com/data/${country}`).then(response => {
        // Save the data for the country
        this.data.push({
          country,
          data: response.data
        });
      }).catch(error => {
        console.error(error);
      });
    });
    console.log(this.countries);
  }
};
